import React from 'react';
import ReactDom,{createPortal} from 'react-dom';
import './css/TodoCss.css';
import EditPopup from './EditPopup';

type TodoPorps = {
  form: string;
  todo: string;
  id: number;
  onRemove: any;
}

const audio = new Audio('eventMusic.mp3');

const stop = () => {
  audio.pause();
  audio.currentTime = 0;
}

const Todo: React.FC<TodoPorps> = ({form, todo, id, onRemove}) => {
  return(
    <main className='todo-list'>
      <div className='title'>
      할 일 목록
      </div>
      <div className="list">
        <section className='forms'>
          날짜 : {form}
        </section>
        <div id = "ad"> </div>
        {createPortal(<div>test </div>, document.getElementById('ad'))}

        <div className = "Todo-Edit">
          <img onMouseLeave={() => stop()} onMouseOver={() => audio.play()}  className = "Todo-Edit-Img" src = "EditImg.png"/>
        </div>

        <img onMouseLeave={() => stop()} onMouseOver={() => audio.play()} onClick={() => onRemove(id)} className = "Todo-Exit-Img" src="ExitImg.png"/>
        <section className='todos'>
          - 할 일<br/><div className="todo-text">{todo}</div>
        </section>
      </div>

    </main>
  )
};

export default Todo;
