import React, {useEffect, useState,Component} from 'react';
import Todo from './Todo';
import './css/AppCss.css';

class App extends Component{
  constructor(props:any){
    super(props)
    this.onSubmit = this.onSubmit.bind(this);
  }

  state = {
    arr: []
  };

  year: string = this.class_date().getFullYear().toString();
  month: string = this.class_date().getMonth().toString();
  day: string = this.class_date().getDay().toString();

  class_date() : Date{
    return new Date();
  }

  text_date(year: string, month: string, day: string) : string{
    return year+'년 '+month+'월 '+day+'일';
  }

  view() {
    var data;
    if (this.state.arr.length){
      return <div>아무것도 생성되지 않았습니다.</div>
    } else{
      return (this.state.arr.map((dateData: any) => {
        return (
          <div>
            <Todo form={this.text_date(dateData.year, dateData.month, dateData.day).toString()} todo={dateData.todo}/>
          </div>)}));
    }
  }

  onSubmit(){
      this.setState({arr: [
        ...this.state.arr,
        {year: "2021", month: "7", day: "21", todo: "글을 수정하세요."}
      ]});
  }

  render() {
    return(
      <div className="App">
      <div className="App-Sub">
      <ul>
      <li>{this.view()}</li>
      </ul>
      </div>
      <div className = "App-Sub">
      <div className="App-Img">
      <img onClick={this.onSubmit} className = "App-Add-Img" src="AddImg.png"/>
      </div>
      <div className="App-Img">
      <img className = "App-Exit-Img" src="ExitImg.png"/>
      </div>
      </div>
      </div>
    );
  }

}

export default App;
