import React, {useState, useEffect} from 'react';
import './SliderCss.css';

var index = 0;

const Slider: React.FC = () => {
  const [count, setCount] = useState(0);

  const resetCount = () => {
    setCount(0);
  };

  useEffect(() => {
    start();
  }, handle);

var handle: any = null;

  var addCount = () => {
    window.clearInterval(handle);
      if(count >= 71.5)
      {
        setCount(0);
      }else{
        setCount(count + 14.3);
        index += 1;
        showDot((index == 6 ? index = 0 : index) );
      }
  };

  function time () {

    //handle = null;
    addCount();
    //console.log(handle);
    //console.log('카운트 : '+count);
  }

  const start = () => {
      handle = window.setInterval( () => time(), 3000, []);
  };


  const reduceCount = () => {
    if(!(count <= 0))
    {
      setCount(count - 14.3);
    }
  };

  const dot = document.getElementsByClassName('dot');

  const showDot = (n : number) => {
    for(var i = 0; i < dot.length; i++){
      dot[i].className = dot[i].className.replace(' active', '');
    }
    console.log(n);
      dot[n].className += ' active';
  };
  return(
    <div>
    <div style={{display: 'flex'}}>
    <button onClick={reduceCount}>넘기기</button>
    <div className='SliderMain'>
    <ul className='SliderBox'>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
      <li className="Slider" style={{right: count+"%"}}><img style={{width: '200px'}} src='/img/Kao.jpg'/></li>
    </ul>
    </div>
    <button onClick={addCount}>넘기기</button>
    </div>
    <span className='dot active' onClick={() => showDot(0)}/>
    <span className='dot' onClick={() => showDot(1)}/>
    <span className='dot' onClick={() => showDot(2)}/>
    <span className='dot' onClick={() => showDot(3)}/>
    <span className='dot' onClick={() => showDot(4)}/>
    <span className='dot' onClick={() => showDot(5)}/>
    </div>
  );
};

export default Slider;
