import React from 'react';
import './css/TodoCss.css';
import useSound from 'use-sound';


type TodoPorps = {
  form: string;
  todo: string;
  id: number;
  onRemove: any;
}

const [play] = useSound('eventMusic.mp3');

const Todo: React.FC<TodoPorps> = ({form, todo, id, onRemove}) => {

  return(
    <main className='todo-list'>
      <div className='title'>
      할 일 목록
      </div>
      <div className="list">
        <section className='forms'>
          날짜 : {form}
        </section>

        <div className = "Todo-Edit">
          <img onMouseOver={() => play}  className = "Todo-Edit-Img" src = "EditImg.png"/>
        </div>

        <img onMouseOver={() => play} onClick={() => onRemove(id)} className = "Todo-Exit-Img" src="ExitImg.png"/>

        <section className='todos'>
          - 할 일<br/><div className="todo-text">{todo}</div>
        </section>
      </div>

    </main>
  )
};

export default Todo;
