import ReactDOM from 'react-dom';
import React, {useState} from 'react';
import './css/EditPopupCss.css';

// assuming in your html file has a div with id 'modal-root';
const  popup: object = document.getElementsByClassName('popup');
export class EditPopup extends React.Component {
  constructor(props: EditPopup) {
    super(props);
    this.EditChange = this.EditChange.bind(this);
  }
  
  state = {
    option: false
  };

   EditChange() : void{
    this.setState({option: !this.state.option});
  }

  render() {
    return (
        <div className='popup-inner'>

        <div className="popup-title">
          편집
        </div>

        <div className="popup-date">
          날 짜  <input className='date' type='date'/> <br/>
          </div>

        <div className="popup-text">
          할 일  <input className='text' type='text'/> <br/>
        </div>

        <div className="popup-button">
        <button className="popup-submit" >변경</button>
        </div>

        <div className="popup-img-main">
          <button onClick={() => this.EditChange()} className="popup-img" />
        </div>

        </div>
    );
  }
}


export default EditPopup;
