import React, {useState} from 'react';
import Movie from './Movie';
import MovieForm from './MovieForm';
import './AppCss.css';
import Navbar from './Navbar';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from 'react-router-dom';
import Slider from './Slider';

function App() {
  const [name, setName] = useState('');
  const [year, setYear] = useState('');

  const [movie, setMovie] : any = useState([]);

  const removeMovie = (id : any) => {
    setMovie(movie.filter((m:any) => {
      return m.id !== id;
    }));
  };

  const renderMovie = movie.length ? movie.map((m :any) => {
    return(
      <Movie movie={m} key={m.id} removeMovie={removeMovie}/>
    );
  }) : '추가된 영화가 없습니다.';

  const addMovie = (m : any) => {
    setMovie([
      ...movie,
      m
    ]);
  };
  return(
    <Router>
    <div className="App">
    <Navbar/>
    <Switch>
    <Route path='/movies'>
    <h1>Movie list</h1>
    <hr/>
    <MovieForm addMovie={addMovie}/>
    {renderMovie}
    </Route>
    <Route path='/blog'>
    <h1>Blog</h1>
    <hr/>
    </Route>
    <Route path='/'>
    <h1>Home</h1>
    <hr/>
    <div style={{maxWidth: '600px', textAlign: 'center', margin: '0px auto'}}>
    <Slider/>
    </div>
    </Route>
    </Switch>
    </div>
    </Router>
  );
}
export default App;
