import React,{useState} from 'react';
import './css/TodoCss.css';
import EditPopup from './EditPopup';

type TodoPorps = {
  form: string;
  todo: string;
  id: number;
  onRemove: any;
}

const audio = new Audio('eventMusic.mp3');

const stop = () => {
  audio.pause();
  audio.currentTime = 0;
};

const Todo: React.FC<TodoPorps> = ({form, todo, id, onRemove}) => {
  const [option, setOption] = useState(false);
  return(
    <main className='todo-list'>
      <div className='title'>
      할 일 목록
      </div>
      <div className="list">
        <section className='forms'>
          날짜 : {form}
        </section>


        <div className = "Todo-Edit">
          <img onClick={() => {setOption(!option)}} onMouseLeave={() => stop()} onMouseOver={() => audio.play()}  className = "Todo-Edit-Img" src = "EditImg.png"/>
        </div>

        <img onMouseLeave={() => stop()} onMouseOver={() => audio.play()} onClick={() => alert(id)} className = "Todo-Exit-Img" src="ExitImg.png"/>
        <section className='todos'>
          - 할 일<br/><div className="todo1-text">{todo}</div>
        </section>
      </div>
      <div className='popup' style={option ? {}: {display: "none"}}>
      <EditPopup/>
      </div>
    </main>
  )
};

export default Todo;
