import ReactDOM from 'react-dom';
import React, {useState} from 'react';
import './css/EditPopupCss.css';

type EditProp = {
  onChange: Function,
  form: string,
  todo: string
}

const EditPopup: React.FC<EditProp> = ({onChange, form, todo}) =>{
  const [dateData, setDate] = useState('');
  const [todoData, setTodo] = useState('');

  const change = () => {
    form = dateData;
    todo = todoData;

    alert(todoData+dateData);
  };

    return (
        <div className='popup-inner'>

        <div className="popup-title">
          편집
        </div>

        <div className="popup-date">
          날 짜  <input className='date' onChange={(e) => setDate(e.target.value)} type='date'/> <br/>
          </div>

        <div className="popup-text">
          할 일  <input className='text' onChange={(e) => setTodo(e.target.value)} type='text'/> <br/>
        </div>

        <div className="popup-button">
        <button className="popup-submit" onClick={() => change()}>변경</button>
        </div>

        <div className="popup-img-main">
          <button onClick={() => onChange(false)} className="popup-img" />
        </div>

        </div>
    );
}
export default EditPopup;
