import React, {useState} from 'react';

const MovieForm = ({addMovie} : any) => {
  const [name, setName] = useState('');
  const [year, setYear] = useState('');
  const [name_error, setNameError] = useState('');
  const [year_error, setYearError] = useState('');


  const resetForm = () => {
    setName('');
    setYear('');
  };

  const resetError = () => {
    setNameError('');
    setYearError('');
  };

  const boolForm : any = () => {
    resetError();
    let bool = true;

    if(!name){
      bool = false;
      setNameError('영화제목을 넣어주세요');
    }

    if(!year){
      bool = false;
      setYearError('개봉년도 넣어주세요');
    }
    return bool;
  };

  const onSubmit = (event: any) => {
    event.preventDefault();
    if(boolForm())
    {
      addMovie({
        id: Date.now(),
        title: name,
        year: year
      });
      resetForm();
    }
  };

  const sendError = (type :any) => {
    return (
      <div>
      <div style={{color: 'red', fontSize: '0.8rem', display: 'flex'}}>{type}</div>
      </div>
    );
  };

  return(
    <div className="App">
    <form onSubmit={onSubmit}>
    <input type='text' value={name} placeholder='영화제목' onChange={e => setName(e.target.value)}/>
    {sendError(name_error)}
    <input type='text' value={year} placeholder='개봉년도' onChange={e => setYear(e.target.value)}/>
    {sendError(year_error)}
    <button>
    영화추가
    </button>
    </form>
    </div>
  );
};

export default MovieForm;
