import React, {useEffect, useState,Component} from 'react';
import Todo from './Todo';
import './css/AppCss.css';

class App extends Component{
  constructor(props:any){
    super(props)
    this.onAdd = this.onAdd.bind(this);
    this.onExit = this.onExit.bind(this);
  }

  state = {
    arr: []
  };

  year: string = this.class_date().getFullYear().toString();
  month: string = this.class_date().getMonth().toString();
  day: string = this.class_date().getDay().toString();

  class_date() : Date{
    return new Date();
  }

  text_date(year: string, month: string, day: string) : string{
    return year+'년 '+month+'월 '+day+'일';
  }

  view() {
    var data;
    if (!this.state.arr.length){
      return <img className="Check-Img" src="notFound.png"/>
    } else{
      return (this.state.arr.map((dateData: any) => {
        return (
          <div>
          <li>
          <Todo form={this.text_date(dateData.year, dateData.month, dateData.day).toString()} todo={dateData.todo} id={dateData.id} onRemove={this.onRemove(dateData.id)}/>
          </li>
          </div>)}));
    }
  }

  onAdd() :void{
      this.setState({arr: [
        ...this.state.arr,
        {year: "2021", month: "7", day: "21", todo: "글을 수정하세요.", id: new Date()}
      ]});
  }

  onRemove(id: number) {
    this.setState({arr: this.state.arr.filter((dateData: any) => {
      return (dateData.id !== id);
    })});
  }

  onExit(){

  }

  render() {
    return(
      <div className="App">
      <div className="App-Sub">
      <ul>
      {this.view()}
      </ul>
      </div>
      <div className = "App-Sub">
      <div className="App-Img">
      <img onClick={this.onAdd} className = "App-Add-Img" src="AddImg.png"/>
      </div>
      </div>
      </div>
    );
  }

}

export default App;
